import React, { useEffect, useRef, useState } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';

const MapComponent = ({ onLocationSelect }) => {
    const mapRef = useRef(null);

    // State to hold the marker's position
    const [markerPosition, setMarkerPosition] = useState(null);

    // Set the initial center of the map
    const center = { lat: -3.745, lng: -38.523 }; // Adjust to your preferred center location

    // Handle the click event on the map to get the selected location
    const handleMapClick = (event) => {
        const lat = event.latLng.lat();
        const lng = event.latLng.lng();

        // Update the marker position
        setMarkerPosition({ lat, lng });

        // Call the function with selected coordinates
        onLocationSelect({ lat, lng });
    };

    useEffect(() => {
        // Any additional effects can go here
    }, [onLocationSelect]);

    return (
        <LoadScript googleMapsApiKey="AIzaSyAbrhlteb_a1DkS0Jp1tU9fLD5Hi-j2CrA"> {/* Replace with your actual API key */}
            <GoogleMap
                id="map"
                mapContainerStyle={{ height: '400px', width: '800px' }}
                center={center}
                zoom={10}
                onClick={handleMapClick} // Set up the click event handler
                onLoad={map => (mapRef.current = map)} // Store the map instance if needed
            >
                {/* Render the marker if markerPosition is set */}
                {markerPosition && (
                    <Marker position={markerPosition} />
                )}
            </GoogleMap>
        </LoadScript>
    );
};

export default MapComponent;
