import React, { useEffect, useRef, useState } from 'react';

const MapComponent = ({ onLocationSelect }) => {
  const mapRef = useRef(null);
  const [map, setMap] = useState(null);
  const markerRef = useRef(null); // Ref to hold the marker

  useEffect(() => {
    const loadMap = () => {
      const google = window.google;
      const initialLocation = { lat: -34.397, lng: 150.644 }; // Default coordinates

      // Initialize the map
      const mapInstance = new google.maps.Map(mapRef.current, {
        center: initialLocation,
        zoom: 8,
      });

      // Listen for map clicks
      mapInstance.addListener('click', (event) => {
        const lat = event.latLng.lat();
        const lng = event.latLng.lng();
        const location = { lat, lng };

        // Place or move the marker
        if (markerRef.current) {
          markerRef.current.setPosition(location); // Move the existing marker
        } else {
          const newMarker = new google.maps.Marker({
            position: location,
            map: mapInstance,
          });
          markerRef.current = newMarker; // Store the marker in ref
        }

        // Pass the location to the parent component
        onLocationSelect(location);
      });

      setMap(mapInstance);
    };

    loadMap();
  }, [onLocationSelect]);

  return <div ref={mapRef} style={{ height: '400px', width: '100%' }} />;
};

export default MapComponent;
