import React, { useState } from 'react';
import './ActivityDisplay.css';
import fetchCategoryById from '../Services/activityServices';

const ActivityDisplay = ({ activity, onDelete, onUpdate }) => {
  const [isEditing, setIsEditing] = useState(false); // State to toggle between edit and view mode
  const [updatedActivity, setUpdatedActivity] = useState(activity); // Copy of the activity to hold updated values

  const handleEditClick = () => {
    setIsEditing(true); // Switch to edit mode
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;

    // Keep the input value as a string for numeric fields during input
    setUpdatedActivity({
      ...updatedActivity,
      [name]: value, // Keep as string, convert to number later on save
    });
  };

  const [categoryName, setCategoryName] = useState(''); // State to hold the category name

    useEffect(() => {
        const getCategoryName = async () => {
            try {
                if (activity.categoryId) { // Check if categoryId is present
                    const category = await fetchCategoryById(activity.categoryId); // Fetch category by ID
                    setCategoryName(category.data.name); // Assuming 'name' is part of the response data
                }
            } catch (error) {
                console.error('Error fetching category:', error);
            }
        };
        getCategoryName();
    }, [activity.categoryId]);


  const handleSaveClick = async () => {
    // Convert necessary fields to numbers before saving
    const updatedActivityWithNumbers = {
      ...updatedActivity,
      price: parseFloat(updatedActivity.price) || 0, // Use 0 if parsing fails
      duration: parseFloat(updatedActivity.duration) || 0,
      specialDiscount: parseFloat(updatedActivity.specialDiscount) || 0,
      location: {
        ...updatedActivity.location,
        lat: parseFloat(updatedActivity.location.lat) || 0,
        lng: parseFloat(updatedActivity.location.lng) || 0,
      },
    };

    try {
      await onUpdate(activity._id, updatedActivityWithNumbers); // Call the update function passed as prop
      setIsEditing(false); // Exit edit mode
    } catch (err) {
      console.error('Failed to update activity:', err.message);
    }
  };


  return (
    <div className="activity-card">
      {isEditing ? (
        // Render form fields in editing mode
        <>
          <input
            type="text"
            name="name"
            value={updatedActivity.name}
            onChange={handleInputChange}
            placeholder="Activity Name"
          />
          <input
            type="date"
            name="date"
            value={new Date(updatedActivity.date).toISOString().split('T')[0]}
            onChange={handleInputChange}
          />
          <input
            type="number"
            name="price"
            value={updatedActivity.price}
            onChange={handleInputChange}
            placeholder="Price"
          />
          <input
            type="number"
            name="duration"
            value={updatedActivity.duration}
            onChange={handleInputChange}
            placeholder="Duration (minutes)"
          />
          <input
            type="text"
            name="category"
            value={updatedActivity.categoryId}
            onChange={handleInputChange}
            placeholder="Category"
          />
          <input
            type="text"
            name="ratings"
            value={updatedActivity.ratings}
            onChange={handleInputChange}
            placeholder="Ratings"
          />
          <input
            type="number"
            name="specialDiscount"
            value={updatedActivity.specialDiscount}
            onChange={handleInputChange}
            placeholder="Special Discount (%)"
          />
          <div className="activity-tags">
            {updatedActivity.tags.map((tag, index) => (
              <input
                key={index}
                type="text"
                name={`tag-${index}`}
                value={tag.name}
                onChange={(e) => {
                  const newTags = [...updatedActivity.tags];
                  newTags[index].name = e.target.value;
                  setUpdatedActivity({
                    ...updatedActivity,
                    tags: newTags,
                  });
                }}
                placeholder={`Tag ${index + 1}`}
              />
            ))}
          </div>
          <input
            type="checkbox"
            name="bookingOpen"
            checked={updatedActivity.bookingOpen}
            onChange={(e) =>
              setUpdatedActivity({
                ...updatedActivity,
                bookingOpen: e.target.checked,
              })
            }
          />
          Booking Open
          <input
            type="text"
            name="locationLat"
            value={updatedActivity.location.lat}
            onChange={(e) =>
              setUpdatedActivity({
                ...updatedActivity,
                location: {
                  ...updatedActivity.location,
                  lat: parseFloat(e.target.value), // Convert to float
                },
              })
            }
            placeholder="Location Latitude"
          />
          <input
            type="text"
            name="locationLng"
            value={updatedActivity.location.lng}
            onChange={(e) =>
              setUpdatedActivity({
                ...updatedActivity,
                location: {
                  ...updatedActivity.location,
                  lng: parseFloat(e.target.value), // Convert to float
                },
              })
            }
            placeholder="Location Longitude"
          />

          <button className="save-button" onClick={handleSaveClick}>Save</button>
          <button className="cancel-button" onClick={() => setIsEditing(false)}>Cancel</button>
        </>
      ) : (
        // Display activity details in view mode
        <>
          <h2 className="activity-title">{activity.name}</h2>
          <p className="activity-date">Date: {new Date(activity.date).toLocaleDateString()}</p>
          <p className="activity-price">Price: ${activity.price}</p>
          <p className="activity-duration">Duration: {activity.duration} minutes</p>
          <p className="activity-category">Category: {activity.category}</p>
          <p className="activity-ratings">Ratings: {activity.ratings}/5</p>
          <p className="activity-special-discount">Special Discount: {activity.specialDiscount}%</p>
          <p className="activity-booking-status">Booking Open: {activity.bookingOpen ? "Yes" : "No"}</p>
          <p className="activity-location">
            Location: {activity.location.lat}, {activity.location.lng}
          </p>
          <div className="tags-container">
            {activity.tags.map((tag, index) => (
              <span key={index} className="activity-tag">{tag.name}</span>
            ))}
          </div>
          <div className="activity-buttons">
            <button className="edit-button" onClick={handleEditClick}>Edit</button>
            <button className="delete-button" onClick={() => onDelete(activity._id)}>Delete</button>
          </div>
        </>
      )}
    </div>
  );
};

export default ActivityDisplay;
