import React, { useEffect, useRef, useState } from 'react';
import { GoogleMap, Marker } from '@react-google-maps/api';

const SimpleMapComponent = ({ location }) => {
    const mapRef = useRef(null);

    // State to hold the marker's position
    const [markerPosition, setMarkerPosition] = useState(location);

    // Set the initial center of the map
    const initialCenter = { lat: 30.745, lng: 30.523 }; // Adjust to your preferred center location

    useEffect(() => {
        // Update marker position when location prop changes
        setMarkerPosition(location);
    }, [location]);

    return (
        <GoogleMap
            id="map"
            mapContainerStyle={{ height: '400px', width: '800px' }}
            center={markerPosition} // Center on markerPosition or initialCenter
            zoom={10} // Adjust zoom level as needed
        >
            {/* Render the marker only if markerPosition is defined */}
            {markerPosition && <Marker position={markerPosition} />}
        </GoogleMap>
    );
};

export default SimpleMapComponent;
