// TouristPage.js
import React ,{ useState } from 'react';
import './AdvertiserPage.css'; 


const TourismGovernerPage = () => {
  const [isCreating, setIsCreating] = useState(false); // State to manage the visibility of the form

  const handleCreateButtonClick = () => {
      setIsCreating(true); // Show the form when the button is clicked
  };

  const closeForm = () => {
      setIsCreating(false); // Hide the form
  };

  const onDelete = (museumId) => {
    console.log(`Museum with ID ${museumId} has been deleted`);
    // Add API call here for deletion, e.g.:
    // await axios.delete(`/api/museums/${museumId}`);
  };

  const onUpdate = async (museumId, updatedMuseum) => {
    console.log(`Museum with ID ${museumId} has been updated:`);
    console.log(updatedMuseum);
    // Add API call here for updating, e.g.:
    // await axios.put(`/api/museums/${museumId}`, updatedMuseum);
  };  

  const museum = {
    _id: "12345",
    name: "The Great Museum",
    description: "A historical museum showcasing ancient artifacts.",
    pictures: [
      "https://example.com/picture1.jpg",
      "https://example.com/picture2.jpg"
    ],
    location: {
      city: "Cairo",
      country: "Egypt",
      address: "123 Museum St."
    },
    openingHours: {
      monday: "9:00 AM - 5:00 PM",
      tuesday: "9:00 AM - 5:00 PM",
      wednesday: "9:00 AM - 5:00 PM",
      thursday: "9:00 AM - 5:00 PM",
      friday: "9:00 AM - 5:00 PM",
      saturday: "9:00 AM - 5:00 PM",
      sunday: "Closed"
    },
    ticketPrices: {
      foreigner: 15.0,
      native: 10.0,
      student: 5.0
    },
    museum: true,
    tourismGovernerId: "abc123"
  };


  return (
      <div>
          <h1>Tourism Governer Page</h1>
          <p>Welcome to the Tourism Governer Page!</p>

          {/* Create Activity button */}
          <button className="create-activity-button" onClick={handleCreateButtonClick}>
              Create Musuem/Historical Place
          </button>

          <MuseumDisplay museum={museum} onDelete={onDelete} onUpdate={onUpdate} />

      </div>
  );
};

export default TourismGovernerPage;
